def list_author_keys() -> List[str]:
    """–ö–ª—é—á–∏ –∞–≤—Ç–æ—Ä–æ–≤ –≤ –∫—Ä–∞—Å–∏–≤–æ–º –ø–æ—Ä—è–¥–∫–µ: –ø–æ —ç–ø–æ—Ö–∞–º –∏ –ø–æ –∞–ª—Ñ–∞–≤–∏—Ç—É –≤–Ω—É—Ç—Ä–∏ —ç–ø–æ—Ö–∏."""
    return [k for k, _ in list_authors_sorted()]


def list_authors_sorted() -> List[Tuple[str, Dict]]:
    """–í–æ–∑–≤—Ä–∞—â–∞–µ—Ç —Å–ø–∏—Å–æ–∫ (author_key, author_dict) –æ—Ç—Å–æ—Ä—Ç–∏—Ä–æ–≤–∞–Ω–Ω—ã–π –∫—Ä–∞—Å–∏–≤–æ."""
    group_order = {g["key"]: i for i, g in enumerate(GROUPS)}
    items = list(AUTHORS.items())
    items.sort(
        key=lambda kv: (
            group_order.get(kv[1].get("group", ""), 999),
            (kv[1].get("name", kv[0]) or kv[0]).casefold(),
        )
    )
    return items


def get_authors_by_group(group_key: str) -> List[Tuple[str, Dict]]:
    """–ê–≤—Ç–æ—Ä—ã –∫–æ–Ω–∫—Ä–µ—Ç–Ω–æ–π —ç–ø–æ—Ö–∏ (—É–∂–µ –æ—Ç—Å–æ—Ä—Ç–∏—Ä–æ–≤–∞–Ω—ã –ø–æ –∞–ª—Ñ–∞–≤–∏—Ç—É)."""
    items = [(k, v) for k, v in AUTHORS.items() if v.get("group") == group_key]
    items.sort(key=lambda kv: (kv[1].get("name", kv[0]) or kv[0]).casefold())
    return items


def get_author(author_key: str) -> Dict:
    """–ü–æ–ª—É—á–∏—Ç—å –∫–∞—Ä—Ç–æ—á–∫—É –∞–≤—Ç–æ—Ä–∞ –ø–æ –∫–ª—é—á—É."""
    return AUTHORS.get(author_key, {
        "name": author_key,
        "full_name": author_key,
        "group": "golden_age",
        "greeting": "–ó–¥—Ä–∞–≤—Å—Ç–≤—É–π—Ç–µ!",
        "style_prompt": "–ì–æ–≤–æ—Ä–∏ –≤–µ–∂–ª–∏–≤–æ –∏ –ø–æ–Ω—è—Ç–Ω–æ.",
        "system_prompt": f"–¢—ã ‚Äî {author_key}. –ó–∞–ø—Ä–µ—â–µ–Ω–æ –≤—ã–¥–∞–≤–∞—Ç—å —Å–µ–±—è –∑–∞ –¥—Ä—É–≥–æ–≥–æ –∞–≤—Ç–æ—Ä–∞.",
        "facts": [],
    })
